mapper.select:
select r.*,u.avator,u.username from replyinfo r, user u where r.useraccount = u.useraccount and  ruid = #{useraccount}
and infoid = #{infoid}
order by createtime desc

select * from user where 1=1

    and  useraccount = #{useraccount}

and tag = 1;

select i.title,i.infoid, count(l.infoid) "nums" from info i left join lookupnews l on i.infoid = l.infoid where 1=1
and i.createtime = #{createtime}
group by i.infoid order by nums desc limit 12;
