mapper.select:
select * from brand where tag = 1

    and gcid = #{gcid}


    limit #{limit} ;

        select b.*,m.gcid, count(r.gid) "nums"
from recordgood r,
     milk m,
     brand b
where r.gid = m.gid
  and m.gcid = #{gcid}
  and b.bid = m.bid
group by r.gid
order by nums desc;

select b.bname, count(m.gid) 'nums' from brand b left join milk m on b.bid = m.bid where 1=1
and m.gcid = #{gcid}
group by b.bid
