mapper.select:
        select * from milk m,brand b where m.bid = b.bid and  m.tag = 1 and b.tag = 1
--        根据品牌查询

            and b.bid = #{bid}

--         查询具体商品

            and m.gid = #{gid}


            and stage = #{stage}


            and cata = #{cata}


            and age = #{age}


            and price between #{sprice} and #{eprice}
